import {Component, OnInit} from '@angular/core';
import {Cliente} from "../cliente";
import {ClientesService} from "../../clientes.service";

@Component({
  selector: 'app-clientes-form',
  templateUrl: './clientes-form.component.html',
  styleUrls: ['./clientes-form.component.css']
})
export class ClientesFormComponent implements OnInit{
  cliente: Cliente;
  success: boolean = false;
  errors: String[]

  constructor(private service: ClientesService) {
    this.cliente=new Cliente();

  }
  ngOnInit(): void {
  }


  onSubmit() {
    this.service
      .salvar(this.cliente)
      .subscribe(response => {
        this.success = true;
        this.errors = [];
        this.cliente=response;
      }, errorResponse => {
        this.success = false;
        this.errors=errorResponse.error.errors;
        }
        )

  }
}
<h1 class="mt-4">Clientes</h1>
<ol class="breadcrumb mb-4">
  <li class="breadcrumb-item active">Cadastro</li>
</ol>

<div class="container">
    <form #clienteForm= "ngForm" (ngSubmit)="onSubmit()">
      <div class="row">
        <div class="alert alert-success" role="alert" *ngIf="success==true">
          Cliente salvo/atualizado com sucesso!
        </div>
        <div class="alert alert-danger" role="alert" *ngFor="let erro of errors">
          {{erro}}
        </div>
      </div>

      <div class="row" *ngIf="">
        <div class="col-md-6">
          <div class="form-group">
            <label>ID:</label>
            <input class="form-control" type="text" [ngModel]="cliente.id" name="id" disabled="true">
          </div>
        </div>

        <div class="col-md-6">
          <div class="form-group">
            <label>Data Cadastro</label>
            <input class="form-control"
                   name="dataCadastro" disabled="true" type="text" [ngModel]="cliente.dataCadastro ">
          </div>
        </div>
        <div class="col-md-6">
          <div class="form-group">
            <label>Nome:</label>
            <input class="form-control" type="text" [(ngModel)]="cliente.nome" name="nome"/>
          </div>
        </div>
        <div class="col-md-6">
          <div class="form-group">
            <label>CPF:</label>
            <input class="form-control" type="text" [(ngModel)]="cliente.cpf" name="cpf">
          </div>
        </div>
      </div>

      <div class="row">
        <div class="col-md-2">
          <button class="btn btn-success" type="submit" >
            <i class="fa fa-save"></i>
            Salvar
          </button>
        </div>
      </div>
    </form>
</div>
